import {Layout, Menu} from 'antd';
import React from "react";
// @ts-ignore
import Login from 'ant-design-pro/lib/Login';
import {Alert, Checkbox} from 'antd';

const {Tab, UserName, Password, Mobile, Captcha, Submit} = Login;

const {Header, Content, Footer, Sider} = Layout;
const {SubMenu} = Menu;

/**
 * Class to represent the login page
 */
export default class Loginpage extends React.Component {
    state = {
        notice: '',
        type: 'tab2',
        autoLogin: true,
    };

    /**
     * Function that executes when login button is clicked
     * @param err error if any
     * @param values hash of values that represent the username and password
     */
    onSubmit = (err: any, values: { username: string; password: string; }) => {
        console.log('value collected ->', {
            ...values,
            autoLogin: this.state.autoLogin,
        });
        if (this.state.type === 'tab1') {
            this.setState(
                {
                    notice: '',
                },
                () => {
                    if (!err && (values.username !== 'admin' || values.password !== '888888')) {
                        setTimeout(() => {
                            this.setState({
                                notice: 'The combination of username and password is incorrect!',
                            });
                        }, 500);
                    }
                }
            );
        }
    };

    /**
     * Function called on when a tab is changed
     * @param key
     */
    onTabChange = (key: any) => {
        this.setState({
            type: key,
        });
    };

    /**
     * Function changed on auto login
     * @param e
     */
    changeAutoLogin = (e: { target: { checked: any; }; }) => {
        this.setState({
            autoLogin: e.target.checked,
        });
    };


    /**
     * Overriding Render function
     */
    render() {
        return (
            <div className="login-warp">
                <Login
                    defaultActiveKey={this.state.type}
                    onTabChange={this.onTabChange}
                    onSubmit={this.onSubmit}
                >
                    <Tab key="tab1" tab="Account">
                        {this.state.notice && (
                            <Alert
                                style={{marginBottom: 24}}
                                message={this.state.notice}
                                type="error"
                                showIcon
                                closable
                            />
                        )}
                        <UserName name="Username"/>
                        <Password name="Password"/>
                    </Tab>
                    <Tab key="tab2" tab="Mobile">
                        <Mobile name="mobile"/>
                        <Captcha onGetCaptcha={() => console.log('Get captcha!')} name="captcha"/>
                    </Tab>
                    <div>
                        <Checkbox checked={this.state.autoLogin} onChange={this.changeAutoLogin}>
                            Keep me logged in
                        </Checkbox>
                        <a style={{float: 'right'}} href="">
                            Forgot password
                        </a>
                    </div>
                    <Submit>Login</Submit>
                    <div>
                        Other login methods
                        <span className="icon icon-alipay"/>
                        <span className="icon icon-taobao"/>
                        <span className="icon icon-weibo"/>
                        <a style={{float: 'right'}} href="">
                            Register
                        </a>
                    </div>
                </Login>
            </div>
        );
    }
}
